// Function to search for a query in the Bible data
function searchBible(query) {
    const results = [];

    // Traverse through all books, chapters, and verses
    for (const book of bibleData.books) {
        for (const chapter of book.chapters) {
            for (const verse of chapter.verses) {
                // Check if the verse text contains the search query (case-insensitive)
                if (verse.text.toLowerCase().includes(query.toLowerCase())) {
                    // Collect the matching verse along with book name, chapter number, and verse ID
                    results.push({
                        bookName: book.name,
                        chapterNumber: chapter.id,
                        verseId: verse.id,
                        verseText: verse.text,
                    });
                }
            }
        }
    }
    return results;
}